// StartPage.js
import React, { useState } from "react";

const StartPage = ({ onStartGame }) => {
  const [username, setUsername] = useState("");

  const handlePlay = () => {
    if (username) {
      const isDrawer = Math.random() < 0.5; // Randomly assign role
      onStartGame(isDrawer ? "drawer" : "guesser");
    }
  };

  return (
    <div className="container">
      <div className="logo">DRAWING GUESS</div>
      <div className="avatar-section">
        <div className="avatar"></div>
        <div className="avatar"></div>
        <div className="avatar"></div>
        <div className="avatar"></div>
      </div>
      <div className="input-section">
        <input
          type="text"
          placeholder="Enter your name"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <br />
        <select>
          <option value="english">English</option>
          <option value="spanish">Spanish</option>
          <option value="french">French</option>
        </select>
      </div>
      <button className="button" onClick={handlePlay}>
        Play!
      </button>
      <br />
      <button className="button button-secondary">Create Private Room</button>
    </div>
  );
};

export default StartPage;
