import React, { useRef, useState, useEffect } from "react";
import io from "socket.io-client";

const socket = io();

const DrawingApp = () => {
  const canvasRef = useRef(null);
  const [lineWidth, setLineWidth] = useState(5);
  const [strokeColor, setStrokeColor] = useState("#000000");

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = 800;
    canvas.height = 600;
    const ctx = canvas.getContext("2d");
    ctx.lineWidth = lineWidth;
    ctx.strokeStyle = strokeColor;
    ctx.lineCap = "round";

    let isDrawing = false;

    const startDrawing = (e) => {
      isDrawing = true;
      ctx.beginPath();
      ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
      socket.emit("drawing", { x: e.nativeEvent.offsetX, y: e.nativeEvent.offsetY, lineWidth, strokeColor });
    };

    const draw = (e) => {
      if (!isDrawing) return;
      ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
      ctx.stroke();

      // Emit the current line being drawn
      socket.emit("drawing", { x: e.nativeEvent.offsetX, y: e.nativeEvent.offsetY, lineWidth, strokeColor });
    };

    const stopDrawing = () => {
      isDrawing = false;
      ctx.closePath();
    };

    canvas.addEventListener("mousedown", startDrawing);
    canvas.addEventListener("mousemove", draw);
    canvas.addEventListener("mouseup", stopDrawing);
    canvas.addEventListener("mouseleave", stopDrawing);

    socket.on("drawing", ({ x, y, lineWidth, strokeColor }) => {
      ctx.lineWidth = lineWidth;
      ctx.strokeStyle = strokeColor;
      ctx.lineTo(x, y);
      ctx.stroke();
    });

    return () => {
      canvas.removeEventListener("mousedown", startDrawing);
      canvas.removeEventListener("mousemove", draw);
      canvas.removeEventListener("mouseup", stopDrawing);
      canvas.removeEventListener("mouseleave", stopDrawing);
      socket.off("drawing");
    };
  }, [lineWidth, strokeColor]);

  return (
    <section className="container">
      <div id="toolbar">
        <h1>Draw.</h1>
        <label htmlFor="stroke">Stroke</label>
        <input
          id="stroke"
          type="color"
          value={strokeColor}
          onChange={(e) => setStrokeColor(e.target.value)}
        />
        <label htmlFor="lineWidth">Line Width</label>
        <input
          id="lineWidth"
          type="number"
          value={lineWidth}
          onChange={(e) => setLineWidth(e.target.value)}
        />
        <button onClick={() => {
          const ctx = canvasRef.current.getContext("2d");
          ctx.clearRect(0, 0, 800, 600);
          socket.emit("clear"); // Emit clear event to clear all clients' canvases
        }}>
          Clear
        </button>
      </div>
      <div className="drawing-board">
        <canvas ref={canvasRef} id="drawing-board"></canvas>
      </div>
    </section>
  );
};

export default DrawingApp;
