import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./StartPage.css"; // Import the CSS

const StartPage = () => {
  const [role, setRole] = useState(null); // Store the role (Drawer or Guesser)
  const navigate = useNavigate();

  // Function to handle the Play button click
  const handlePlay = () => {
    // Randomly assign role
    const assignedRole = Math.random() < 0.5 ? "drawer" : "guesser";
    setRole(assignedRole);

    // Navigate to the respective page based on the role
    if (assignedRole === "drawer") {
      navigate("/drawer");
    } else {
      navigate("/guesser");
    }
  };

  return (
    <div className="container">
      <div className="logo">DRAWING GUESS</div>
      <div className="avatar-section">
        <div className="avatar"></div>
        <div className="avatar"></div>
        <div className="avatar"></div>
        <div className="avatar"></div>
      </div>
      <div className="input-section">
        <input type="text" placeholder="Enter your name" />
        <br />
        <select>
          <option value="english">English</option>
          <option value="spanish">Spanish</option>
          <option value="french">French</option>
        </select>
      </div>
      <button className="button" onClick={handlePlay}>
        Play!
      </button>
      <br />
      <button className="button button-secondary">Create Private Room</button>
    </div>
  );
};

export default StartPage;
