import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";

const socket = io();

const GuesserGame = () => {
  const canvasRef = useRef(null);
  const [guess, setGuess] = useState("");
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = 800;
    canvas.height = 600;
    const ctx = canvas.getContext("2d");

    // Fill the canvas with a white background
    ctx.fillStyle = "#FFFFFF";
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    socket.on("drawing", ({ x, y, lineWidth, strokeColor }) => {
      ctx.lineWidth = lineWidth;
      ctx.strokeStyle = strokeColor;
      ctx.lineTo(x, y);
      ctx.stroke();
    });

    socket.on("clear", () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.fillStyle = "#FFFFFF"; // Refill with white background
      ctx.fillRect(0, 0, canvas.width, canvas.height);
    });

    return () => {
      socket.off("drawing");
      socket.off("clear");
    };
  }, []);

  const submitGuess = () => {
    if (guess) {
      socket.emit("guess", { content: guess });
      setGuess("");
    }
  };

  const sendMessage = () => {
    if (message) {
      socket.emit("chat message", message);
      setMessage("");
    }
  };

  return (
    <div className="container">
      <h1>Guesser Game</h1>
      <div className="guess-section">
        <input
          type="text"
          value={guess}
          onChange={(e) => setGuess(e.target.value)}
          placeholder="Type your guess..."
        />
        <button onClick={submitGuess}>Submit Guess</button>
      </div>
      <div className="drawing-board">
        <canvas ref={canvasRef} id="drawing-board"></canvas>
      </div>
      <div className="chat-room">
        <div className="chat-box">
          {chat.map((item, index) => (
            <div key={index} className={`chat-message ${item.type}`}>
              {item.type === "opponent" ? `Opponent: ${item.content}` : `Guess: ${item.content}`}
            </div>
          ))}
        </div>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default GuesserGame;
