// App.js
import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch, useHistory } from "react-router-dom";
import StartPage from "./components/StartPage";
import Guesser from "./components/GuesserGame"; // Create this component
import Drawer from "./components/DrawingApp"; // Create this component

const App = () => {
  const [role, setRole] = useState("");
  const history = useHistory();

  const handleStartGame = (selectedRole) => {
    setRole(selectedRole);
    history.push(`/${selectedRole}`); // Navigate to either /guesser or /drawer
  };

  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <StartPage onStartGame={handleStartGame} />
        </Route>
        <Route path="/Guesser">
          <Guesser />
        </Route>
        <Route path="/drawer">
          <Drawer />
        </Route>
      </Switch>
    </Router>
  );
};

export default App;
