import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import io from "socket.io-client";

const socket = io("http://localhost:12345");

const StartPage = ({ onStartGame }) => {
    const [username, setUsername] = useState("");
    const navigate = useNavigate(); // Ensure you're using useNavigate

    const handlePlay = () => {
        if (username) {
            socket.emit("joinGame", (role) => {
                console.log("Role assigned:", role); // Check if role is received
                if (role === "drawer") {
                    navigate("/drawer"); // Navigate to Drawer App
                } else {
                    navigate("/guesser"); // Navigate to Guesser Game
                }
            });
        } else {
            console.log("Username is required"); // Log if username is empty
        }
    };

    return (
        <div className="container">
            <div className="logo">DRAWING GUESS</div>
            <div className="input-section">
                <input
                    type="text"
                    placeholder="Enter your name"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
            </div>
            <button className="button" onClick={handlePlay}>
                Play!
            </button>
        </div>
    );
};

export default StartPage;
