import React, { useState, useEffect } from "react";
import io from "socket.io-client";

const socket = io();

const GuesserGame = () => {
  const [guess, setGuess] = useState("");
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);

  useEffect(() => {
    socket.on("chat message", (msg) => {
      setChat((prev) => [...prev, { type: "opponent", content: msg }]);
    });
    socket.on("guess", (data) => {
      setChat((prev) => [...prev, { type: "guess", content: data.content }]);
    });
  }, []);

  const submitGuess = () => {
    if (guess) {
      socket.emit("guess", { content: guess });
      setGuess("");
    }
  };

  const sendMessage = () => {
    if (message) {
      socket.emit("chat message", message);
      setMessage("");
    }
  };

  return (
    <div className="container">
      <h1>Guesser Game</h1>
      <div className="guess-section">
        <input
          type="text"
          value={guess}
          onChange={(e) => setGuess(e.target.value)}
          placeholder="Type your guess..."
        />
        <button onClick={submitGuess}>Submit Guess</button>
      </div>
      <div className="chat-room">
        <div className="chat-box">
          {chat.map((item, index) => (
            <div key={index} className={`chat-message ${item.type}`}>
              {item.type === "opponent" ? `Opponent: ${item.content}` : `Guess: ${item.content}`}
            </div>
          ))}
        </div>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default GuesserGame;
